
angular.module('environment',[]).provider('environmentService',function(){this.environment='development';this.data={};this.config=function(config){this.data=config;};this.set=function(environment){this.environment=environment;};this.get=function(){return this.environment;};this.read=function(variable){if(variable==='all'){return this.data.vars[this.get()];}
else{return this.data.vars[this.get()][variable];}};this.is=function(environment){if(environment===this.environment){return true;}
else{return false;}};this.check=function(){var	hostname=window.location.hostname,self=this;angular.forEach(this.data.domains,function(v,k){angular.forEach(v,function(v){if(hostname.search(v)!==-1){self.environment=k;}});});};this.$get=function(){return this;};});